<?php/*Plugin Name: aaM Facebook Stick BoxPlugin URI: https://wordpress.org/plugins/aam-facebook-stick-box/Description: This plugin will add a Stick Box on the Right Side of Website, which contain a facebook page, when an user move mouse pointer on the box the facebook like page will apper.Author: Md. Abdullah Al MahimAuthor URI: http://fb.com/a.a.mahinVersion: 2.0.1*/if ( ! defined( 'ABSPATH' ) ) { die; } // Cannot access pages directly.require_once(dirname(__FILE__) . "/inc/class/aam_settings_api.php");require_once(dirname(__FILE__) . "/inc/admin_setting_panel.php");// Enque Required Style & Scriptsfunction aam_fb_stick_box_libs() {	wp_enqueue_script('jquery');	wp_enqueue_script('aam-fb-stick-box-ready-js', plugin_dir_url( __FILE__ ).'js/aam-fb-stick-ready.js', array('jquery'));	wp_enqueue_style('aam-fb-stick-box-custome-style', plugin_dir_url( __FILE__ ).'css/aam-fb-stick.css');}add_action( 'wp_enqueue_scripts', 'aam_fb_stick_box_libs' );/*-- Enque Style's For Admin Panel --*/function aam_fb_box_admin_plugin_styles() {    wp_enqueue_style( 'stylish-admin-panel-plugin-style', plugin_dir_url( __FILE__ ).'css/admin_css.css' );}add_action( 'admin_enqueue_scripts', 'aam_fb_box_admin_plugin_styles' );/*--FB Stick box Content --*/function aam_fb_stick_content() {$old_setting_page_name = get_option('aam_fb_page');$new_setting_page_name = aam_fb_get_option( 'aam_fb_page_url', 'aam_fbb_general_setting_sec','https://www.facebook.com/facebook');if($old_setting_page_name){	if($new_setting_page_name=='https://www.facebook.com/facebook'){		$aam_fb_page = $old_setting_page_name;	}else{		$aam_fb_page = $new_setting_page_name;	}}else{	$aam_fb_page = $new_setting_page_name;}ob_start();?>	<div class="aam_fb_stick" style=""> 	 <div>	   <iframe src="http://www.facebook.com/plugins/likebox.php?href=<?php echo $aam_fb_page; ?>&amp;width=245&amp;colorscheme=light&amp;show_faces=true&amp;border_color=white&amp;connections=12&amp;stream=false&amp;header=false&amp;height=270" scrolling="no" frameborder="0" scrolling="no" style="none; overflow: hidden; height: 290px; width: 245px;background:#fafafa;"></iframe><span>Plugin by:<a href="http://fb.com/a.a.mahin/" target="_blank">aaM</a></span>	  </div>	</div><?php$content = ob_get_clean();echo $content;}add_action( 'wp_footer', 'aam_fb_stick_content' );